INSERT INTO `books_book` VALUES (27, '薄如晨曦', ' [法] 玛丽斯·孔戴', '9787522518114', '2023-09-01', '2023-09-19 04:06:09.384939', '语言，文学', '中文', '九州出版社', 62.00, '布克奖入围、新学院奖得主，法语文学重要女作家玛丽斯·孔戴代表作首次引进！\n\n“猎巫”为背景，以严肃写实+瑰丽想象，\n\n书写底层女性的成长与觉醒，\n\n赞颂每一个桀骜、自由、勇敢的女性。', 'http://localhost:8000/static/books/9787522518114.jpg');

